var nav;var sticky;function stickyMenu(){if(window.pageYOffset>sticky){nav.addClass("sticky-nav")}else{nav.removeClass("sticky-nav")}}$(document).ready(function(){AOS.init({once:true});nav=$("nav");sticky=$(window).height()-80;stickyMenu();window.onscroll=stickyMenu;$('[data-toggle="tooltip"]').tooltip();$("a.nav-link").on("click",function(){var a=$(this).attr("href");var b;if($(a).length>=1){b=$(a).offset().top-80}else{b=$("a[name="+a.substr(1,a.length-1)+"]").offset().top-80}$("html,body").animate({scrollTop:b},1000);$(this).blur();return false})});$(window).resize(function(){sticky=$(window).height()-80;stickyMenu()});$(window).on("load",function(){$("body").css({overflow:"inherit"});$("#loader").fadeOut()});